}

.split-block p:nth-of-type(2) {
}

#workflow {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 20px;
  grid-column: 1/-1;
  list-style: none;
}

#workflow ul {
  list-style: none;
  padding: 20px;
}

#workflow h2:nth-of-type(1) {
  border-bottom: 1px solid rgba(150, 150, 150, 0.5);
  padding: 20px;
}

#workflow div:first-of-type {
  grid-column: 1;
  border-right: 1px solid rgba(150, 150, 150, 0.5);
  padding: 20px;
}

#workflow div:nth-of-type(2){
  grid-column: 2;
  padding: 20px;
}

#my-work {
  margin: 100px 0px;
  grid-column: 1/-1;
}

#my-work h3 {
  color: #fff;
}

#my-work div {
  display: flex;
  flex-wrap: wrap;
  color: #fff;
}

#my-work div figure:nth-of-type(1) {
  background: linear-gradient(rgba(0,0,0,.6),rgba(0,0,0,.6)), url('~assets/header-background2.jpg');
  background-size: cover;
  background-repeat: no-repeat;
}

#my-work div figure:nth-of-type(2) {
  background: linear-gradient(rgba(0,0,0,.6),rgba(0,0,0,.6)), url('~assets/header-background2.jpg');
  background-size: cover;
  background-repeat: no-repeat;
}

#my-work div figure:nth-of-type(3) {
  background: linear-gradient(rgba(0,0,0,.6),rgba(0,0,0,.6)), url('~assets/header-background2.jpg');
  background-size: cover;
  background-repeat: no-repeat;
}

#my-work div figure:nth-of-type(4) {
  background: linear-gradient(rgba(0,0,0,.6),rgba(0,0,0,.6)), url('~assets/header-background2.jpg');
  background-size: cover;
  background-repeat: no-repeat;
}

#my-work figure {
  border-radius: 0.375rem;
  border: none;
  margin: 20px;
  padding: 10px 25px;
  flex: 25;
  height: 300px;
}

#my-work button {
  justify-items: right;
}
#hobbies {
  grid-column: 1/-1;
  background: #f2f2f2;
  min-height: 300px;
  margin: 100px 0px;
  padding: 50px;
  border-radius: 0.375rem;
  text-align: center;
}

#hobbies ul {
  display: flex;
  list-style: none;
}

#hobbies ul li {
  padding: 20px;
}

#hobbies p {
  text-align: center;
}

#hobbies svg {
  width: 100px;
  height: 100px;
}

#hobbies ul li:nth-of-type(odd) {
  padding: 60px;
}

#hobbies svg {
  padding-top: 20px;
}

